    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(jTextField1.getText().equals(""))
        {
            JOptionPane.showMessageDialog(null, "Existem campos n√£o preenchidos");
        }
        else if(jTextField1.getText().length() < 3)
        {
            JOptionPane.showMessageDialog(null, "Preencher o nome completo");
        }
        else
        {
            try
            {
                Vendedores ven = new Vendedores();
                ven.setNomeVendedor(jTextField1.getText());
                ven.Inserir();
                JOptionPane.showMessageDialog(null, "Cadastrado com sucesso !");
                jTextField1.setText("");
            }
            catch (ErrorHandle e)
            {
                JOptionPane.showMessageDialog(null, e.getMessage(), "Erro ao salvar Vendedor", 0);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

